mapper.insert:
/* 새 게시물(사진) 등록 */
INSERT INTO photos (USER_ID, IMAGE_URL, CAPTION, CREATED_AT)
VALUES (#{userId}, #{imageUrl}, #{caption}, NOW())

mapper.update:
/* 게시물(사진) 정보 수정 */
UPDATE photos
SET IMAGE_URL = #{imageUrl},
    CAPTION = #{caption}
WHERE ID = #{id}

mapper.delete:
/* 게시물 id로 삭제 */
DELETE FROM photos
       WHERE USER_ID = #{userId}
         AND ID = #{id}
